      let candiadateToJob = api.get(`/api/candidate/job/${idJob}`);
      let getLane = api.get(`/api/lanes`);
      const [dataJob, dataCanidate, dataLane] = await Promise.all([jobDetail, candiadateToJob, getLane]);

      if (dataJob && dataCanidate) {
        setTimeout(() => {
          self.setState({
            isLoading: false,
            isLoadingTable: false,
            data: dataJob.data,
            dataCandidate: dataCanidate.data.list,
            viewCandidate: dataCanidate.data.list.slice(0, this.state.numberInPage),
            totalRow: dataCanidate.data.list.length,
            isDisplay: dataCanidate.data.list.length === 0,
            arrayLane: dataLane.data.lane
          });
        }, 800);
      }
    } catch (err) {
      console.log(err);
    }
  }

  async getDataCandidate() {
    try {
      let self = this;
      this.setState({
        isLoadingTable: true
      });
      const idJob = this.props.match.params.id;
      let {
        numberInPage,
        pageNumber
      } = this.state;
      let response = await api.get(`/api/candidate/job/${idJob}`);

      if (response) {
        setTimeout(() => {
          self.setState({
            // isLoading: false,
            dataCandidate: response.data.list,
            viewCandidate: response.data.list.slice((pageNumber - 1) * numberInPage, (pageNumber - 1) * numberInPage + numberInPage),
            totalRow: response.data.list.length,
            isDisplay: response.data.list.length === 0
          });
        }, 200);
        setTimeout(() => {
          self.setState({
            isLoadingTable: false
          });
        }, 800);
      }
    } catch (err) {
      console.log(err);
    }
  }

  async handlePagination(page) {
    let {
      numberInPage,
      dataCandidate
    } = this.state;
    let viewCandidate = dataCandidate.slice((page - 1) * numberInPage, (page - 1) * numberInPage + numberInPage);
    await this.setState({
      viewCandidate,
      pageNumber: page
    });
  }

