import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import Network from "../../Service/Network";
import { Form } from "react-bootstrap";
import EditorCustom from "./EditorCustom";
import EditorCustomTwo from "./EditorCustomTwo";
import { ToastContainer, toast } from "react-toastify";
import CustomToast from "../common/CustomToast";
import { connect } from "react-redux";

import "./style.css";
const api = new Network();
var timer = null;
var timerTwo = null;

var aboutFetch = `<h2><span style="color: rgb(21, 188, 197);font-size: 16px;"><strong> 1. INTRODUCTION</strong></span></h2><h3 style="font-size: 16px;"><strong>a. About Fetch:</strong></h3>
<p>Fetch Technology Vietnam is a comprehensive global provider of HR and Talent Acquisition Services, focusing primarily in the technology fields. Founded in 2016, Fetch Technology Vietnam helps foreign companies of all types and sizes reach their potential by providing the talent and support to efficiently build and scale a high-performing, distributed workforce in Vietnam.</p>
<p>Our mission is to offer Vietnam’s most talented technologists a platform to connect with some of the world’s leading tech companies and build their expertise on a global scale. Over 4 years, Fetch has built a good reputation and is trusted by many Vietnamese and foreign companies; And Fetch will continue its good work to bridge the divide between the World and the Vietnam Tech sector.</p>`;
var responsibiltties = `<h2 style="font-size: 16px;"><span style="color: rgb(21, 188, 197);"><strong>2. RESPONSIBILITIES</strong></span></h2><br/>`;
var benefit = `<h2 style="font-size: 16px;"><span style="color: rgb(21, 188, 197);"><strong>4. WHY YOU‘LL LOVE WORKING HERE</strong></span></h2><br/>`;
var requirement = `<h2 style="font-size: 16px;"><span style="color: rgb(21, 188, 197);"><strong>3. REQUIREMENT</strong></span></h2><br/>`;

class AddJob extends Component {
  constructor(props) {
    super(props);
    this.editorOne = React.createRef();
    this.editorThree = React.createRef();
    this.editorFour = React.createRef();
    this.editorFive = React.createRef();
    this.editorSix = React.createRef();

    this.scrollClient = React.createRef();

    this.state = {
      styleHeader: "card-header",
      isLoading: false,
      data: {
        title: "",
        location: "",
        locationId: "",
        type: "",
        time: "",
        salary: "",
        content: "",
        slug: "",
        jobStatus: "Active",
        aboutFetch: "",
        client: null,
        responsibilities: "",
        benefit: "",
        requirement: "",
        niceToHave: "",
        metaJob: "",
        titlePage: "",
        keyword: "",
        note: "",
        descJob: "",
        interviewProcess: "",
        extraBenefit: "",
        arr_skill: [],
        arr_skill_required: [],
        user: [],
      },
      val: "",
      valTwo: "",
      suggestion: [],
      suggestionTwo: [],
      listSkill: [],
      listLocation: [],
      listClient: [],
      validated: false,
      isOpenAddSkill: false,
      newSkill: "",
    };
  }

  getInitData = async () => {
    try {
      let getListLocation = api.get(`/api/location`);
      let getListClient = api.get(`/api/all/client`);
      let getListSkill = api.get(`/api/all/skill`);
      let [dataLocation, dataClient, dataSkill] = await Promise.all([
        getListLocation,
        getListClient,
        getListSkill,
      ]);
      if (dataLocation && dataClient && dataSkill) {
        if (this._isMounted) {
          await this.setState({
            listLocation: dataLocation.data.location,
            listClient: dataClient.data.clients,
            listSkill: dataSkill.data.skills,
          });
        }
      }
    } catch (error) {
      console.log(error);
    }
  };

  setDataEditor = () => {
    this.editorOne.current.setContent(aboutFetch);
    this.editorThree.current.setContent(responsibiltties);
    this.editorFour.current.setContent(requirement);
    this.editorSix.current.setContent(benefit);
  };

  componentDidMount() {
    this._isMounted = true;
    window.addEventListener("scroll", this.handleScroll);
    this.setDataEditor();
    this.getInitData();
  }

  componentWillUnmount() {
    this._isMounted = false;
    window.removeEventListener("scroll", this.handleScroll);
  }

  handleScroll = () => {
    let lengthScroll = window.pageYOffset;
    if (lengthScroll > 220) {
      this.setState({
        styleHeader: "card-header style-header-job",
      });
    } else {
      this.setState({
        styleHeader: "card-header",
      });
    }
  };

  findSkill = async (keyword) => {
    try {
      if (keyword) {
        const skill = await api.post("/api/skill/admin/search", {
          skill: keyword,
        });
        this.setState({ suggestion: skill });
      } else {
        this.setState({ suggestion: [] });
      }
    } catch (e) {
      console.log(e);
    }
  };

  findSkillTwo = async (keyword) => {
    var self = this;
    const { data } = this.state;
    try {
      if (keyword) {
        const skill = await api.post("/api/skill/admin/search", {
          skill: keyword,
        });
        /* Check Trung Skill*/
        if (data.arr_skill_required.length > 0) {
          var filteredArray = skill.filter(function (array_el) {
            return (
              data.arr_skill_required.filter(function (anotherOne_el) {
                return anotherOne_el.id == array_el.id;
              }).length == 0
            );
          });
          this.setState({ suggestionTwo: filteredArray });
        } else {
          this.setState({ suggestionTwo: skill });
        }
      } else {
        this.setState({ suggestionTwo: [] });
      }
    } catch (e) {
      console.log(e);
    }
  };

  handleSkillRequired = (e) => {
    let skill = e.target.value;
    clearTimeout(timer);
    timer = setTimeout(() => {
      this.findSkill(skill);
    }, 1000);
    this.setState({ val: e.target.value });
  };

  handleSkill = (e) => {
    let skill = e.target.value;
    clearTimeout(timerTwo);
    timerTwo = setTimeout(() => {
      this.findSkillTwo(skill);
    }, 1000);
    this.setState({ valTwo: e.target.value });
  };

  // select skill required and set state
  selectSkillRequired = (suggess, index, e) => {
    const { data } = this.state;
    data.arr_skill_required.push(suggess);
    this.setState({ data: data, suggestion: [], val: "" });
  };

  // select skill and set state
  selectSkill = (suggess, index, e) => {
    const { data } = this.state;
    data.arr_skill.push(suggess);
    this.setState({ data: data, suggestionTwo: [], valTwo: "" });
  };

  // delete tag skill required
  removeTag = (position) => {
    const { data } = this.state;
    data.arr_skill_required.splice(position, 1);
    this.setState({ data: data, suggestion: [] });
  };

  // delete tag skill
  removeTagSkill = (position) => {
    const { data } = this.state;
    data.arr_skill.splice(position, 1);
    this.setState({ data: data, suggestionTwo: [] });
  };

  handleChange = (event) => {
    const { data } = this.state;
    const name = event.target.name;
    const value = event.target.value;
    data[name] = value;
    this.setState({
      data: data,
    });
  };

  handleSelectClient = (event) => {
    const { data } = this.state;
    const name = event.target.name;
    const value = event.target.value;
    data[name] = {
      id: value,
    };
