  setTagSkill = async () => {
    let cata = await this.editorFour.current.getContent();
    console.log(cata);
    let temp_tag = [];
    const { listSkill } = this.state;
    console.log(listSkill);
    for (let i = 0; i < listSkill.length; i++) {
      if (cata.toLowerCase().indexOf(listSkill[i].name.toLowerCase()) !== -1) {
        console.log("====> có");
        temp_tag.push(listSkill[i]);
      }
      if (temp_tag.length === 6) break;
    }
    const { data } = this.state;
    data.arr_skill_required = temp_tag;
    this.setState({
      data: data,
    });
  };

  setTagSkillNotRequired = async () => {
    let self = this;
    let cata = await this.editorFive.current.getContent();
    let temp_tag_two = [];
    const { listSkill } = this.state;
    for (let i = 0; i < listSkill.length; i++) {
      if (cata.toLowerCase().indexOf(listSkill[i].name.toLowerCase()) !== -1) {
        temp_tag_two.push(listSkill[i]);
      }
      if (temp_tag_two.length === 5) break;
    }
    // Check Trung Skill
    const { data } = this.state;
    if (data.arr_skill_required.length > 0) {
      var filteredArray = temp_tag_two.filter(function (array_el) {
        return (
          data.arr_skill_required.filter(function (anotherOne_el) {
            return anotherOne_el.id == array_el.id;
          }).length == 0
        );
      });
      data.arr_skill = filteredArray;
      this.setState({
        data: data,
      });
    } else {
      data.arr_skill = temp_tag_two;
      this.setState({
        data: data,
      });
    }
  };

  handleSubmit = async (event) => {
    const form = event.currentTarget;
    let currentTargetRect = event.target.getBoundingClientRect();
    const event_offsetY = -currentTargetRect.top;
    event.preventDefault();
    event.stopPropagation();
    if (form.checkValidity() === false) {
      window.scroll({
        top: event_offsetY - 1800,
        left: 0,
        behavior: "smooth",
      });
      if (this.state.data.client == null || this.state.data.client == "") {
        window.scrollTo({
          left: 0,
          top: this.scrollClient.current.offsetTop,
          behavior: "smooth",
        });
      }
      this.setState({ validated: true });
      return;
    }

    const { data, listLocation } = this.state;
    let arrSkill = [];
    if (data.arr_skill_required.length > 0) {
      data.arr_skill_required.map((item) => {
        arrSkill.push({ id: item.id, isRequired: true });
      });
    }
    if (data.arr_skill.length > 0) {
      data.arr_skill.map((item) => {
        arrSkill.push({ id: item.id, isRequired: false });
      });
    }
    let location = listLocation.find((item) => item.id === data.locationId);
    let content = `${location.descLocation}<p><span style="color: rgb(136, 136, 136);">${data.type}</span></p>`;
    const valueOne = this.editorOne.current.getContent();
    const valueThree = this.editorThree.current.getContent();
    const valueFour = this.editorFour.current.getContent();
    const valueFive = this.editorFive.current.getContent();
    const valueSix = this.editorSix.current.getContent();

    let self = this;
    try {
      const dataJob = {
        title: data.title,
        content: content,
        locationId: data.locationId,
        type: data.type,
        time: data.time,
        salary: data.salary,
        jobStatus: data.jobStatus,
        keyword: data.keyword,
        note: data.note,
        titlePage: data.titlePage,
        metaJob: data.metaJob,
        clientId: Number(data.client.id) || null,
        skill: arrSkill,
        aboutFetch: valueOne || "",
        responsibilities: valueThree || "",
        requirement: valueFour || "",
        niceToHave: valueFive || "",
        benefit: valueSix || "",
        descJob: data.descJob,
        interviewProcess: data.interviewProcess,
        extraBenefit: data.extraBenefit,
      };
      this.setState({ validated: false, isLoading: true });
      // console.log(dataJob);
      const response = await api.post(`/api/jobs`, dataJob);
      if (response.data.success) {
        setTimeout(() => {
          self.setState({
            isLoading: false,
          });
          toast(<CustomToast title={"Post Job Success!"} />, {
            position: toast.POSITION.BOTTOM_RIGHT,
            autoClose: 3000,
            className: "toast_login",
            closeButton: false,
            hideProgressBar: true,
            newestOnTop: true,
            closeOnClick: true,
            rtl: false,
            pauseOnFocusLoss: true,
            draggable: true,
            pauseOnHover: true,
          });
        }, 1000);
      } else {
        toast(
          <CustomToast
            title={"Something went wrong please try again later!"}
            type="error"
          />,
          {
            position: toast.POSITION.BOTTOM_RIGHT,
            autoClose: 3000,
            className: "toast_login",
            closeButton: false,
            hideProgressBar: true,
            newestOnTop: true,
            closeOnClick: true,
            rtl: false,
            pauseOnFocusLoss: true,
            draggable: true,
            pauseOnHover: true,
          }
        );
      }
    } catch (err) {
      console.log(err);
      toast(
        <CustomToast
          title={"Something went wrong please try again later!"}
          type="error"
        />,
        {
          position: toast.POSITION.BOTTOM_RIGHT,
          autoClose: 3000,
          className: "toast_login",
          closeButton: false,
          hideProgressBar: true,
          newestOnTop: true,
          closeOnClick: true,
          rtl: false,
          pauseOnFocusLoss: true,
          draggable: true,
          pauseOnHover: true,
        }
      );
    }
  };

