                            <p>
                              You can add new skills{" "}
                              <span
                                onClick={this.handleOpenAddSkill}
                                className="add-skill-here"
                              >
                                here
                              </span>
                              .
                            </p>
                          </div>
                        </div>
                        <div
                          className={
                            this.state.isOpenAddSkill
                              ? "row"
                              : "row hidden-create-skill"
                          }
                        >
                          <div className="col-md-2" />
                          <div className="col-md-10">
                            <div className="contain-new-skill">
                              <Form.Control
                                name="newSkill"
                                type="text"
                                value={this.state.newSkill}
                                onChange={this.hanedleNewSkill}
                                className="input-skill-hk"
                              />
                              <div
                                onClick={this.handleCreateSkill}
                                className="btn btn-primary btn-shadow-hover font-weight-bold mr-2"
                              >
                                Add
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                      <div className="col-xl-1" />
                    </div>
                  </div>
                </div>
              </Form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  return {
    className_wrap_broad: state.ui.className_wrap_broad,
  };
};

export default connect(mapStateToProps)(AddJob);
